package cs3500.set;import cs3500.set.controller.FeaturesImpl;import cs3500.set.controller.FeaturesInterface;import cs3500.set.model.hw02.Card;import cs3500.set.model.hw02.SetGameModel;import cs3500.set.model.hw02.SetThreeGameModel;import cs3500.set.model.hw03.AbstractSetGameModel;import cs3500.set.model.hw03.GeneralSetGameModel;import cs3500.set.view.SetGameGuiView;import cs3500.set.view.SwingGuiView;/** * class that runs the game. In this case that game is the set three game. */public class SetGame {  /**   * Runs the program using the "main" keyword.   *   * @param args represents a list of arguments passed onto the method.   */  public static void main(String[] args) {    AbstractSetGameModel threeModel = new SetThreeGameModel();    AbstractSetGameModel generalModel = new GeneralSetGameModel();    if (args[0].equalsIgnoreCase("three")) {      int row = Integer.parseInt(args[1]);      int col = Integer.parseInt(args[2]);      playGame(threeModel, row, col);    }    if (args[0].equalsIgnoreCase("general")) {      int row = Integer.parseInt(args[1]);      int col = Integer.parseInt(args[2]);      playGame(generalModel, row, col);    }  }  /**   * Plays the game of Set with the given model as a parameter implementation.   * @param model represents the model that the game should be based on. It represents   *              the rules to play by.   */  public static void playGame(SetGameModel<Card> model, int rows, int cols) {    model.startGameWithDeck(model.getCompleteDeck(), rows, cols);    SetGameGuiView view = new SwingGuiView(model);    FeaturesInterface controller = new FeaturesImpl(model, view);    view.addFeatures(controller);  }}