package cs3500.set;import java.io.InputStreamReader;import cs3500.set.controller.SetGameController;import cs3500.set.controller.SetGameControllerImpl;import cs3500.set.model.hw02.Card;import cs3500.set.model.hw02.SetGameModel;import cs3500.set.model.hw02.SetThreeGameModel;import cs3500.set.model.hw03.AbstractSetGameModel;import cs3500.set.model.hw03.GeneralSetGameModel;import cs3500.set.view.SetGameTextView;import cs3500.set.view.SetGameView;/** * class that runs the game. In this case that game is the set three game. */public class SetGame {  /**   * Runs the program using the "main" keyword.   *   * @param args represents a list of arguments passed onto the method.   */  public static void main(String[] args) {    AbstractSetGameModel threeModel = new SetThreeGameModel();    AbstractSetGameModel generalModel = new GeneralSetGameModel();    if (args[0].equalsIgnoreCase("general")) {      playGame(generalModel);    }    if (args[0].equalsIgnoreCase("three")) {      playGame(threeModel);    }  }  /**   * Plays the game of Set with the given model as a parameter implementation.   * @param model represents the model that the game should be based on. It represents   *              the rules to play by.   */  public static void playGame(SetGameModel<Card> model) {    Readable rd = new InputStreamReader(System.in);    Appendable ap = System.out;    SetGameView view = new SetGameTextView(model, ap);    SetGameController controller = new SetGameControllerImpl(model, view, rd);    controller.playGame();  }}