package cs3500.set.controller;import cs3500.set.model.hw02.Coord;/** * Represents the controller for the game of Set. */public interface FeaturesInterface {  /**   * Takes in the row and column of the card and checks if it makes a valid set.   *   * @param row represents the row of the card that was clicked.   * @param col represents the column of the card that was clicked.   */  void input(int row, int col);  /**   * Takes in the row and column of the card and checks if it makes a valid set.   *   * @param coord represents the coordinates of the card that was clicked.   */  void input(Coord coord);  /**   * Starts the game.   */  void playGame();}