package cs3500.set.view;import org.junit.Assert;import org.junit.Test;import cs3500.set.model.hw02.Card;import cs3500.set.model.hw02.Count;import cs3500.set.model.hw02.Filling;import cs3500.set.model.hw02.SetThreeGameModel;import cs3500.set.model.hw02.Shape;import cs3500.set.model.hw03.AbstractSetGameModel;/** * Class to test the SetGameView. */public class SetGameViewTest {  AbstractSetGameModel game1;  SetGameView gameView;  Card card1;  Card card2;  /**   * Tests the toString method for the SetGameTextView class.   */  @Test  public void testToStringGameView() {    this.game1 = new SetThreeGameModel();    this.game1.startGameWithDeck(game1.getCompleteDeck(), 3, 3);    this.gameView = new SetGameTextView(this.game1);    Assert.assertEquals("1EO 1EQ 1ED\n"            + "1SO 1SQ 1SD\n"            + "1FO 1FQ 1FD", this.gameView.toString());  }  /**   * Tests IllegalArgumentException for SetGameTextView class.   */  @Test(expected = IllegalArgumentException.class)  public void testIllegalArgumentExceptionForView() {    this.gameView = new SetGameTextView(null);  }  /**   * Tests toString method for the Card class.   */  @Test  public void testToStringForCard() {    this.card1 = new Card(Count.ONE, Filling.EMPTY, Shape.SQUIGGLE);    Assert.assertEquals("1EQ", this.card1.toString());    this.card2 = new Card(Count.THREE, Filling.STRIPED, Shape.DIAMOND);    Assert.assertEquals("3SD", this.card2.toString());  }}